function [P] = newton_interpolation(x,y)  %NEWTON_INTERPOLATION(x,y)  %x,y               are row matrices of same length which represent the   %                  datapoints to interpolate    if length(x) != length(y)     error("vectors must have same dimentions");  endif  n = length(x);  F = zeros(n,n);   for i = 1:n    F(i, 1) = y(i);  end  for i = 2:n    for j = 2:i      F(i,j) = (F(i,j-1)-F(i-1,j-1))/(x(i)-x(i-j+1));    end  end  P = diag(F);  P = P';    printf("\n\nP(x) = %d", P(1));  for i = 2:n    if P(i) > 0      printf(" + ");     else printf(" - ");    end    printf("%d(x - %d)", abs(P(i)), x(1));    for j = 2:i-1      printf("(x - %d)", x(j));    end  end  printf("\n\n");  
endfunction
