function [P,val] = newton_interpolation(to_find,x,y)  %NEWTON_INTERPOLATION(x,y)  %to_find           the point which you want to find  %x,y               are row matrices of same length which represent the   %                  datapoints to interpolate    if length(x) != length(y)     error("vectors must have same dimentions");  endif  n = length(x);  F = zeros(n,n);   for i = 1:n    F(i, 1) = y(i);  end  for i = 2:n    for j = 2:i      F(i,j) = (F(i,j-1)-F(i-1,j-1))/(x(i)-x(i-j+1));    end  end  P = diag(F)';  val = P(1);  for i = 2:n    a = P(i);    for j = 2:i      a *= (to_find - x(j-1));    end    val += a;  end  printf("\n\nValue interpolated: %.6f\n",val); 
endfunction
